INSERT INTO `sheet1` VALUES ('悲惨世界', '克劳德-米歇尔·勋伯格', '180分钟', '苦役犯冉·阿让因一块面包入狱19年，被主教感化后重生，却遭警探沙威终身追捕。街垒战中，理想主义者高唱《Do You Hear the People Sing?》赴死——黑暗时代的人性光辉。', '音乐剧', '革命·救赎', '法式', '悲壮', 'https://nfassetoss.southcn.com/__asset/08567fcd74/3c527f9e52.jpg?r=0.7836725103940556');
INSERT INTO `sheet1` VALUES ('歌剧魅影', '安德鲁·劳埃德·韦伯', '150分钟', '巴黎歌剧院地下的毁容天才“魅影”，培养少女克里斯汀成为巨星，却因爱生恨。1吨重水晶吊灯砸向观众席的瞬间，爱情与疯狂同时爆发。', '音乐剧', '爱情·疯癫', '法式', '悲剧', 'https://ts1.tc.mm.bing.net/th/id/R-C.ce246f078638d74991f321c58e309ffc?rik=8JgtLDp1s2cGZw&riu=http%3a%2f%2fc4-q.mafengwo.net%2fs10%2fM00%2f21%2fB9%2fwKgBZ1lWY5SAFNo2AAKT3d7qWjs64.jpeg&ehk=Yue9DtWo1jmaSgVuUwczm3J%2bD57v%2bJO3mSM9tm%2fSexA%3d&risl=&pid=ImgRaw&r=0');
INSERT INTO `sheet1` VALUES ('国王与我', '理查德·罗杰斯 & 奥斯卡·汉默斯坦二世', '150分钟', '19世纪60年代，英国寡妇安娜受聘为暹罗王室教授西方文化，与专制国王在东西方文化碰撞中渐生情愫。舞台以《汤姆叔叔的小屋》芭蕾舞剧展现文化冲突，最终国王在临终前接受变革。', '音乐剧', '文化冲突·爱情 ', '美式', '正剧', 'https://i.gtimg.cn/qqlive/img/jpgcache/files/qqvideo/hori/3/37sufbw2patoauw.jpg');
INSERT INTO `sheet1` VALUES ('天鹅湖', '娜塔莉亚·玛卡洛娃', '120分钟', '王子爱上被诅咒的白天鹅，却在舞会上错认黑天鹅。当双人舞变成三角拉扯，羽毛与谎言一起坠落——唯有真爱之死能破解魔咒。', '芭蕾舞剧', '童话爱情', '俄式', '悲剧', 'https://ts1.tc.mm.bing.net/th/id/R-C.320a97be21c7b48a537a85684d908672?rik=smDmvPQ%2bpCf0KA&riu=http%3a%2f%2fpic.baike.soso.com%2fp%2f20131221%2f20131221140719-1312183619.jpg&ehk=NsD46BokDrdzOBEmnJNFPYw0iUZVW601E%2bYC79RJmuw%3d&risl=&pid=ImgRaw&r=0');
INSERT INTO `sheet1` VALUES ('猫', 'Trevor Nunn（原版）／Joel Ferrell（巡演）', '140分钟', '月夜的垃圾场，杰里科猫群盛装登场。老猫格里泽贝拉唱起《Memory》，回忆里曾经的金色年华与如今的落魄交织。当晨光升起，她缓缓走向重生的天梯——每只猫都在问：我是否值得被记住？', '音乐剧', '群像·生命赞歌', '百老汇', '悲喜交织', 'nan');
INSERT INTO `sheet1` VALUES ('Superhero 超能学校', '杰瑞米', '70分钟', 'Sammy是一个拥有超能力的男孩，但他只想做一个普通人，因为他的超能力是让周围的一切变成音乐剧！每当他快乐、生气或是悲伤时，他和身边的人都会控制不住地开始唱歌跳舞。他的父母受不了这样的日子，把他送去了Hero High，一所专为超能者所设立的学校。在这里，Sammy见到了许多千奇百怪的超能力，也因为自己的“音乐剧”超能力把学校搞得一团糟。与此同时，一个阴谋正在暗处滋生，Miss Menace和她的助手Mr. Plop正在密谋掌控这所学校。Sammy只有一个机会证明：他是一个真正的英雄；与别人不同也没那么糟糕。 ', '青少年音乐剧', '校园·成长', '英伦', '喜剧', 'https://img.piaoniu.com/my_file/2d0b1946-dc41-4f9e-bf3d-d4fa4c0ca9cd.jpg');
INSERT INTO `sheet1` VALUES ('一个无政府主义者的意外死亡', '孟京辉', '110分钟', '改编自诺奖得主同名政治讽刺剧。一名“疯子”乔装成法官，将警局黑幕层层剥开：刑讯、伪造畏罪自杀、媒体共谋……笑声中露出獠牙，荒诞里滴着鲜血。', '先锋话剧', '政治讽刺', '意式', '黑色喜剧', 'https://img.chinaticket.com/admin/2025/06/11/28c675bb6ec50ebd_60_85.jpg');
INSERT INTO `sheet1` VALUES ('开心聊斋·三生PLUS', '李唯贺', '120-150分钟', '书生兴于唐科举高中，却被主考官胡大人“狸猫换太子”，将状元头衔强塞给自己的傻儿子。兴于唐含冤而死，在奈何桥边控诉不公。阎王审理此案，因双方各执一词，索性让二人带着前世记忆进入轮回，开启三生三世冤冤相报的奇幻旅程。故事融合古风奇幻与现代职场梗，如孟婆卖汤KPI、阎王职场PUA等，笑点密集。', '古风舞台剧', '奇幻·职场讽刺  ', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/11/93ea4de43e7d554e_60_85.jpg');
INSERT INTO `sheet1` VALUES ('纳尔齐斯与歌尔德蒙', '谷亦安', '120分钟', '把黑塞同名小说折叠进舞台。冷修士纳尔齐斯与流浪艺术家歌尔德蒙，一个追求精神绝对，一个沉溺感官尘网；两条人生弧线在声光、肢体与圣咏中交汇，问“灵魂与肉身，究竟谁拯救谁”。', '文学改编音乐剧', '哲学成长', '意式', '悲剧', 'https://img.chinaticket.com/admin/2025/06/20/148e743ce36a1316_60_85.jpg');
INSERT INTO `sheet1` VALUES ('叶甫盖尼·奥涅金', '里马斯·图米纳斯', '150分钟', '大雪覆盖的庄园里，忧郁贵族奥涅金撕裂了塔季扬娜的爱情，也撕裂了自己的灵魂。舞台被设计成可旋转的“冰湖”，记忆与诗行在其上滑行——“我年轻、愚蠢，我浪费了一切”。', '话剧', '爱情·诗体小说', '俄式', '悲剧', 'https://img.chinaticket.com/admin/2025/05/22/436f6dcf31b2d205_60_85.jpg');
INSERT INTO `sheet1` VALUES ('萨蒂之名·春之祭', '谢欣', '75分钟', '以萨蒂钢琴乐与《春之祭》原始节奏为骨骼，舞者如祭祀图腾般起伏。谢欣用极简肢体与数学般精准的队列，重译“人类以舞蹈向死而生”的古老命题。', '当代舞剧', '音乐与祭祀', '法式', '悲剧', 'https://img.chinaticket.com/admin/2025/06/24/e7b7d3cafe2e8a6a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('锦衣卫之刀与花', '张博', '100分钟', '刀折花落 是非浮沉 谁能定论 唯将盛世永存 刀与花 历史传奇的诗意凝练 风舞阳，一个草根少年，误打误撞间成为锦衣卫的一员。偶然间偷听的一场密谋中，将他的命运瞬间悬于刀锋之上…… 飞鱼服里藏刀影，明月难窥半夜魂。成立于洪武年间的锦衣卫，本是皇帝的贴身卫队和情报机构，围绕秘密侦察、奉旨缉拿、监视百官的行动，其神秘身份和历史传说为创作者提供了丰富的戏剧素材。 锦衣卫天然与权势勾连，既是利刃般的工具，也在风云变幻间，成为斗争的牺牲品。', '国风音乐剧', '武侠·悬疑', '国风', '悲剧', 'https://img.piaoniu.com/poster/47116c50c098bb87507b40123f88a6bac0d85adc.jpg');
INSERT INTO `sheet1` VALUES ('她对此感到厌烦', '何齐', '120分钟', '三个时空的女性在同一张餐桌上轮流崩溃，又彼此递纸巾。她们谈论月经、升职、母职与离婚，像把“女性日常”放在显微镜下，看厌倦如何成为新的能量。', '小剧场话剧', '女性主义·现实', '都市', '喜剧', 'https://img.piaoniu.com/poster/0a88e91b4ea93b536c2d321b6ca5f07d6ea69a54.jpg');
INSERT INTO `sheet1` VALUES ('永不消逝的电波', '韩真&周莉亚', '135分钟', '上海街头，雨夜。 李侠与兰芬假扮夫妻，开启12年的潜伏生涯…… 每一天，行走在刀锋， 每一晚，秘密情报传送千里。 一重重伪装，藏起疲惫，眺望曙光。 解放前夕，黎明将至，夜色更加阴冷。 李侠的身边，无形的敌人步步逼进，无数战友消失在疾风骤雨。 报社秘书、摄影记者、裁缝掌柜、小学徒、黄包车夫、社长、卖花女， 他们的身份究竟是真是假？ 无声的枪口，紧张的追逐，闪电撕裂心的伤口，吞声饮泪…… 一路患难走来的李侠夫妇，面临生死抉择。 李侠坦然发送最后一份情报，笑对牺牲。 天，快亮了。 李侠，以李白等地下工作者烈士为原型， 他们，隐姓埋名，以生命丈量光明的历程。 他们相信，苟利国家生死以，岂因祸福避趋之？ 长河无声，滔滔东去，电波不绝，信念永存。 ', '谍战舞剧', '革命·爱情', '海派', '悲剧', 'https://img.chinaticket.com/admin/2025/05/23/b07dff3a93bb9003_60_85.jpg');
INSERT INTO `sheet1` VALUES ('回廊亭杀人事件', '赵淼', '110分钟', '密闭回廊、暴雨夜、三具尸体、四段独白。东野圭吾小说被切成拼图式舞台，观众与演员一起“在回字形走廊里无限循环”，寻找真正的复仇者。', '悬疑话剧', '悬疑推理', '日式', '悲剧', 'https://img.chinaticket.com/admin/2025/04/17/ff41115d0f6cd175_60_85.jpg');
INSERT INTO `sheet1` VALUES ('我送你红色玫瑰', '暂未公布', '110分钟', '盲人调音师与外卖女孩因一束迟到的红玫瑰相遇，在钢琴键与手机送餐提示音的交错里，两个“不被看见”的人互相点亮黑暗。', '现实主义话剧', '都市治愈爱情', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/07/5f8aa3c64005253c_60_85.jpg');
INSERT INTO `sheet1` VALUES ('寻找声音的耳朵', '张忱婷', '110分钟', '男孩聃聃天生能听到万物的秘密旋律，却被贴上“多动症”标签。当他决定闭上耳朵，世界也失去了颜色——一部关于“听见自己，也被世界听见”的音乐童话。', '儿童音乐剧', '成长·亲子', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/05/262f764cdeae39fa_60_85.jpg');
INSERT INTO `sheet1` VALUES ('玛丽亚·卡拉斯', '帕布罗·拉雷恩', '123分钟', '1977 年巴黎，一代歌剧女神在病痛与镁光灯夹击下回到空无一人的剧院，与记忆里每一个角色共舞——美狄亚、托斯卡、诺玛……当最后一个高音消散，她把自己的生命永远定格在最辉煌的咏叹里', '传记歌剧电影', '艺术家传记', '欧美', '悲剧', 'https://img.chinaticket.com/admin/2025/07/04/facd0375fcb87f02_60_85.jpg');
INSERT INTO `sheet1` VALUES ('来听你喜欢的歌', '暂未公布', '100分钟', '深夜电台直播间内，一位过气 DJ 与一位失语症少女用点歌单完成对话——每一首歌都是一把钥匙，打开彼此不愿触碰的昨天。', '华语音乐剧', '都市情感', '华语', '喜剧', 'https://img.piaoniu.com/poster/6c2fba23cc434a7ecbe09c6fba3cee9063e1555e.jpg');
INSERT INTO `sheet1` VALUES ('罗密欧与朱丽叶', '巴蒂斯特·勒莫瓦纳', '150分钟', '维罗纳的阳台被浓缩成一座 7 米高的可移动铁架，两位少年在金属冷光中完成永恒一吻。摇滚乐队现场轰鸣，让“旧故事”长出“新伤口”：爱永远年轻，死亡也是。', '摇滚音乐剧', '古典改编·爱情悲剧', '法式', '悲剧', 'https://img.piaoniu.com/poster/5a582da0afda75208624abbcc58f9f8ec7b3ac3c.jpg');
INSERT INTO `sheet1` VALUES ('三湾，那一夜', '殷弘毅', '110分钟', '中国国家话剧院原创话剧《三湾，那一夜》取材于1927年9月29日晚至30日凌晨，以毛泽东同志在江西永新县三湾村领导“三湾改编”这一历史事件为背景，聚焦于三湾那一夜决定中国革命前途命运的会议。《三湾，那一夜》艺术化表现了“三湾改编”这一建党建军史上的里程碑事件，生动展现了毛泽东同志在风云激荡中熔铸人民军队之魂的政治远见和艰难历程。剧中重点阐释了“党指挥枪”“支部建在连上”“官兵平等”等思想精髓，是我们党和人民军队走向胜利的思想法宝。 剧情简介： 为什么“支部建在连上”对中国革命有着如此重大的历史意义？为什么在部队建立士兵委员会，会遇到那么多阻力？ 毛泽东运用怎样的政治智慧，使一支一盘散沙的队伍，一夜之间，奇迹般地“散沙聚成水门汀“？ 三湾，那一夜，那个杂货铺里的会议，如同中国共产党人“扛什么旗、干什么事、走什么路”的风向标，一座标有“不忘初心、方得始终”的金矿，留给后人去挖掘，去映照现在与未来。', '革命历史话剧', '党史·群像', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/25/3cc7aabf6c7d1463_60_85.jpg');
INSERT INTO `sheet1` VALUES ('倒不过的时差', '李伯男', '100分钟', '本剧改编自一个真实的新闻故事。 一对中国夫妇，倾尽全力送儿子到美国留学。儿子在美国与同是留学生的中国姑娘相识相恋并结婚。老夫妻卖掉国内的房产，随儿到美国养老，为儿子买房、带孩子。不曾想，由于不适应异国他乡的文化，两代人在观念上产生了巨大差异，最终引发了一场令人扼腕的悲剧…… 这部剧在当下演出具有很强的现实意义。落叶归根、养儿防老思想严重的中国老人，在人情淡漠、追求独立个性的美国养老，本身就充满着冒险，也必然会矛盾重重。作为一个社会悲剧，搬上舞台，能引人警醒。', '都市话剧', '婚姻·现实', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/07/16/97934bc22748a444_60_85.jpg');
INSERT INTO `sheet1` VALUES ('费加罗的婚礼', '让·路易·马丁', '180分钟', '一天之内，仆人费加罗用机智与幽默拆解伯爵的“初夜权”阴谋，婚礼进行曲响起时，整座塞维利亚都在合唱“爱情与自由不可买卖”。', '歌剧', '爱情·社会讽刺', '意式', '喜剧', 'https://img.chinaticket.com/admin/2025/05/19/1a7722c0104b8cbc_60_85.jpg');
INSERT INTO `sheet1` VALUES ('两个人的谋杀', '斯科特·兰德里', '110分钟', '钢琴家亚瑟在台上弹着《月光》，台下却躺着两具尸体——观众成了陪审团，凶手就在你我中间。全剧仅两位演员扮演 13 个角色，钢琴与阿卡贝拉随时变证据。', '悬疑音乐剧', '悬疑推理·犯罪', '欧美', '喜剧', 'https://img.chinaticket.com/admin/2025/05/15/f9c2da0d7093d5a8_60_85.jpg');
INSERT INTO `sheet1` VALUES ('杨家岭的春天', '王晓鹰', '120分钟', '抗战烽火中，鲁迅艺术学院师生在黄土坡上搭起草台班子：木刻刀当道具，麦秸垛做布景，用一出《白毛女》唤醒千沟万壑的民心——舞台成为最锋利的武器。', '革命史诗音乐剧', '抗战救亡·文艺', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/06/cd802de1e32b44a8_60_85.jpg');
INSERT INTO `sheet1` VALUES ('认真的重要性', '王可然', '140分钟', '维多利亚时代的伦敦，两位花花公子编造“厄内斯特”假名逃避婚姻，却在同一天遇见真命天女。王尔德用毒舌金句告诉世人：认真，你才是人生赢家。', '英式喜剧话剧', '讽刺·爱情', '英伦', '喜剧', 'https://img.chinaticket.com/admin/2025/06/21/30e3547cb2907537_60_85.jpg');
INSERT INTO `sheet1` VALUES ('长翅膀的杜若', '杨浠堃', '90分钟', '一只折翼的机械鸟在末日后荒漠寻找水源，遇见失忆少女杜若。她们用残存的诗稿和齿轮修补天空，却发现“飞翔”原是人类最后的谎言。', '当代舞剧', '末世·成长', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/07/05/019a5a351608e414_60_85.jpg');
INSERT INTO `sheet1` VALUES ('摇滚红与黑', 'François Chouquet（原版）／中文版导演刘令飞', '135分钟', '司汤达名著被搬进硬摇滚现场。野心家于连在吉他失真中攀爬社会阶梯，血红灯光劈开十九世纪的虚伪沙龙——爱与权力，都在鼓点里爆裂。', '摇滚音乐剧', '爱情·复仇', '法式', '悲剧', 'https://img.piaoniu.com/poster/aa8c5783ebbc1c2afce84729f9755410765f465f.jpg');
INSERT INTO `sheet1` VALUES ('万尼亚舅舅', '尤里·布图索夫（俄罗斯）／李六乙（2024国话版）', '160分钟', '四幕乡村静夜里，万尼亚把一生的徒劳磨成一把钝刀，刺向自己。契诃夫的笑与泪在幽暗灯光下交替——生活没有奇迹，只有延宕的叹息。', '现实主义话剧', '家庭', '俄式', '悲剧', 'https://img.chinaticket.com/admin/2025/05/22/c559413730a1ae23_60_85.jpg');
INSERT INTO `sheet1` VALUES ('狐妖小红娘', '刘天池', '140分钟', '涂山苏苏为了成为“红线仙”，必须在人妖两界牵起三对有情人。国漫 IP 首次舞台化，水幕投影让“转世续缘”变成肉眼可见的流星雨。', '国漫改编音乐剧', '动漫奇幻·爱情', '国风', '喜剧', 'https://img.chinaticket.com/admin/2025/05/10/d098b35236312e17_60_85.jpg');
INSERT INTO `sheet1` VALUES ('海底两万里', '菲利普·柯蓝德法国）／中文版导演赵淼', '120分钟', '鹦鹉螺号破浪前行，纱幕与水下投影把潜艇驶进观众席，凡尔纳的深海探险触手可及。', '科幻冒险舞台剧', '科幻·探险', '法式', '正剧', 'https://img.chinaticket.com/admin/2025/04/21/42d30213246d57d0_60_85.jpg');
INSERT INTO `sheet1` VALUES ('第一次约会', '比尔· Berry（Broadway原版）／中文版导演丁美婷', '100分钟', '社恐程序员与元气插画师的相亲夜，尴尬短信全唱成歌；当《Can’t Help Falling in Love》响起，尴尬变浪漫。', '百老汇浪漫音乐剧', '浪漫喜剧·都市爱情', '百老汇', '喜剧', 'https://img.chinaticket.com/admin/2025/06/07/bf2913839e3b68c5_60_85.jpg');
INSERT INTO `sheet1` VALUES ('人生的盛宴', '王翀', '105分钟', '观众围坐在12米长桌旁，演员端上真实热菜：童年是一勺甜到发腻的冰淇淋，婚姻是带骨头的牛排，死亡是一杯无色无味的水。每一次咀嚼都是一段人生切片，当餐盘见底，长桌中央只剩一盏忽明忽暗的灯。', '沉浸式戏剧', '人生哲理', '国风', '悲喜交织', 'https://img.chinaticket.com/admin/2025/06/24/3fbaa59d1a01443a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('海上钢琴师', '大卫·贝尔', '140分钟', '1900生于船、死于船，从未踏上陆地。旋转甲板与实时投影让巨浪打进观众席，当小号手Max吹响最后一曲《Playing Love》，船体缓缓下沉，钢琴与大海一起把故事永远封存在那间空荡荡的锅炉房。', '剧情音乐剧', '音乐·孤独', '意式', '悲剧', 'https://img.chinaticket.com/admin/2025/06/06/bc31ea942e9d6eb5_60_85.jpg');
INSERT INTO `sheet1` VALUES ('瞧!这个人', '文小超', '75分钟', '尼采、耶稣、卓别林在同一具身体里轮番登场：尼采高举锤子砸向偶像，耶稣在十字架阴影里低语，卓别林用拐杖敲打世界的荒诞。病床、追光、独白交织成一场关于“人如何成为人”的深夜审判。', '独角肢体剧', '存在主义', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/05/19/924fcb4f04c91ae8_60_85.jpg');
INSERT INTO `sheet1` VALUES ('我爱XXX', '孟京辉', '90分钟', '1994年的舞台上，演员用“我爱——”加任意名词轰炸空气：我爱原子弹，我爱高跟鞋，我爱隔壁王大爷……观众从爆笑到沉默，语言在狂欢中失去意义，只剩心跳证明我们还在爱。', '先锋实验话剧', '语言游戏', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/17/9df17cd79af606c1_60_85.jpg');
INSERT INTO `sheet1` VALUES ('喜剧奇妙夜', '刘亮佐', '120分钟', '三对演员轮番砸挂“段子手的生活真相”：脱口秀吐槽996，漫才拆解婆媳关系，即兴现场把观众手机变成剧情道具。当最后一个包袱落地，笑声背后全是都市人的小伤口。', '综艺式喜剧秀', '浪漫喜剧·都市幽默', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/14/0240d54eb12fbb7f_60_85.jpg');
INSERT INTO `sheet1` VALUES ('动物狂欢节', '张之益', '70分钟', '森林小动物为拯救家园，用音乐对抗黑风暴：刺猬打鼓、长颈鹿拉琴、小狐狸吹响萨克斯。当《天鹅》旋律响起，3D毛发在银幕上随风颤动，孩子们第一次听见“环保”也能如此动听。', '3D音乐动画电影', '童话', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/16/3fdde8010fda0afe_60_85.jpg');
INSERT INTO `sheet1` VALUES ('这家伙不赖', '刘天池', '110分钟', '小镇青年冒充海归博士，用100个谎言换一份爱情。观众手持投票器决定他是否值得原谅——每一次选择都让结局走向不同的荒诞与温柔。', '互动戏剧话剧', '身份错位', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/07/12/25cf5287c6fd61f9_60_85.jpg');
INSERT INTO `sheet1` VALUES ('泰坦尼克号', '托姆·萨瑟兰（Broadway版）／中文版导演约瑟夫·格雷夫斯', '135分钟', '1912年4月，皇家邮轮泰坦尼克号启航。锅炉工、船长、设计师、富豪、移民、恋人……不同阶级的真实命运随冰山撞击沉入海底。当船头没入乐池，乐队仍在甲板演奏《更近我主》，观众与角色一起屏住最后一口气。', '百老汇史诗音乐剧', '灾难·群像', '百老汇', '悲剧', 'https://img.chinaticket.com/admin/2025/07/05/60c6f3134646c319_60_85.jpg');
INSERT INTO `sheet1` VALUES ('婚礼大镖客', '关旭', '100分钟', '民国年间，土匪误劫新娘花轿，镖师与媒婆被迫“假成亲”。刀枪不入的土匪遇上三寸不烂之舌的媒婆，喜堂秒变武林大会；当唢呐响起，红绸与刀光一起飞舞，笑到最后的竟是爱情。', '民国荒诞喜剧', '动作·爱情', '国风', '喜剧', 'https://img.chinaticket.com/admin/2025/07/16/a3980fe8909c050b_60_85.jpg');
INSERT INTO `sheet1` VALUES ('贼想得到你', '雷·库内（英国）／中文版导演魏玮', '120分钟', '一颗价值连城的钻石、五伙各怀鬼胎的盗贼、一座随时旋转的酒店。门一开错，贼就撞进另一场骗局；英式黑色幽默在错位的房门与尖叫声中层层加码，直到观众也分不清谁才是真正的倒霉蛋。', '英式黑色喜剧', '犯罪·误会', '英式', '喜剧', 'https://img.chinaticket.com/admin/2025/07/16/4cf846f3dfa4742e_60_85.jpg');
INSERT INTO `sheet1` VALUES ('寻找李二狗', '李宗熹', '100分钟', '北漂编剧回老家奔丧，却发现同村竟有17个“李二狗”。一场啼笑皆非的寻人启事，牵出三代人的乡愁、秘密与和解：当最后一个李二狗被找到，村口的老槐树也悄悄掉下一片叶子。', '温情喜剧话剧', '乡土·亲情', '华语', '喜剧', 'https://img.piaoniu.com/mtl/default/img/8277cZ7Kn7_.png');
INSERT INTO `sheet1` VALUES ('音乐地理课', '暂未公布', '70分钟', '指挥叔叔带小朋友“乘音符去旅行”：非洲鼓点敲出草原心跳，苏格兰风笛吹过高地山谷。每一次节奏敲击，地图上就亮起一盏小灯——原来世界可以用耳朵丈量。', '儿童教育音乐会', '音乐启蒙', '华语', '喜剧', 'https://img.piaoniu.com/my_file/f714f850-7225-4b3e-83ea-62e427c81570.jpg');
INSERT INTO `sheet1` VALUES ('记忆深处', '佟睿睿', '90分钟', '以南京大屠杀幸存者李秀英、张纯如等真实人物为原型，通过现代舞与多媒体结合，再现历史伤痛。舞台用碎裂的镜面象征记忆碎片，舞者在血与火的投影中挣扎——有些记忆必须被铭记，即使痛彻心扉。  ', '现代舞剧', '历史·战争  ', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/26/adc641a82f099ac9_60_85.jpg');
INSERT INTO `sheet1` VALUES ('粉丝来信', '高瑞嘉', '140分钟', '1943年，上海。一则备受关注的绯闻渐渐传开：刚逝世的小说家海鸣和女作家夏光的最后一份私人信件即将被公开出版。而出版人李灵因参与了《朝华》杂志的编辑工作，现被关押在日本宪兵队拘留所。十分崇拜海鸣的郑微岚找到李灵。为了目睹这封“粉丝来信”，微岚和李灵开始慢慢回忆起了“夏光”的神秘往事。 时间拉回 1941 年，公共租界内，作家们组成了“七人会”，坚守着《朝华》杂志。初入编辑室的郑微岚，心中有一个秘密：他曾以“夏光”的笔名和海鸣长期写信通讯。而在此期间，“夏光”的形象就在他笔下不断完善，慢慢生长……她是缪斯女神般的存在。她的出现给海鸣和微岚带来了光，也同样带来了噩梦…… ', '音乐剧', '文学·心理', '华语', '悲剧', 'https://img.piaoniu.com/my_file/2a57f429-9543-4c9d-9a75-73bc05a86c5f.jpg');
INSERT INTO `sheet1` VALUES ('等待戈多', '孟京辉', '120分钟', '两个流浪汉日复一日地苦等“戈多”，可“戈多”从未出现。世界瞬息万变，人生则是一场漫无目的的等待。终生等待，终生寻觅意义所在。', '荒诞话剧', '哲学·存在', '法式', '荒诞', 'https://img.chinaticket.com/admin/2025/04/23/561c75a007056d5f_60_85.jpg');
INSERT INTO `sheet1` VALUES ('只此青绿', '周莉亚、韩真', '110分钟', '舞蹈诗剧《只此青绿》以“诗剧”为体裁，以“展卷、问篆、唱丝、寻石、习笔、淬墨、入画”等篇章为纲目，观众将跟随一位现代故宫研究员——展卷人的视角，徜徉在富有传奇色彩的中国传统美学意趣之中。 全剧采用时空交错式的叙事结构。剧情发生时间设定为现实中《千里江山图》即将展出之际、也是千年之前少年画家希孟即将完稿之时，展卷人因对《千里江山图》的潜心钻研，走入了希孟的内心，伴其历经了呕心沥血绘制《千里江山图》的宝贵时光，与这位只有寥寥数字记载的“天才少年”心心相印，探寻出了《千里江山图》“独步千载”的偶然与必然，读懂了国宝文物与现代人之间的情感连接。 舞台上将勾勒出层次丰富的画境：一轮明月朗照千年，展卷人在月光下与希孟四目相对；兢兢业业的故宫博物院文博工作者们与勤勉不辍的古代工匠们共同汇成一幅情景交融的人文画卷。 ', '舞蹈诗剧', '国宝·传承', '国风', '诗意', 'https://img.chinaticket.com/admin/2025/07/03/3f9850b7837cb485_60_85.jpg');
INSERT INTO `sheet1` VALUES ('命运之上', '赵淼', '90分钟', '罕见病患者刘大铭经历11次手术，用脊柱钢钉支撑身体写作。舞台用悬吊的病床与投影病历，展现“向死而生”的意志——肉体困于方寸，灵魂翱翔九天。', '独角戏', '励志·生命', '华语', '正剧', 'https://img.piaoniu.com/poster/4afa3dfcaa903d55ff9ac2997fc631b8cf2b6367.jpg');
INSERT INTO `sheet1` VALUES ('骆驼祥子', '梅阡', '150分钟', '上世纪二十年代，军阀混战，时局动荡。人力车夫祥子在刘四爷开办的人和车厂靠拉车为生，他年轻健壮，吃苦耐劳，争强好胜，最大的愿望就是依靠自己的汗水挣得一辆属于自己的洋车，当个“体面的”车夫。 刘四爷的女儿，三十岁的老姑娘虎妞爱上了祥子，不顾父亲的反对下嫁。刘四爷暗自卖掉了车厂，一个人带着钱远走高飞。祥子和虎妞买了一辆新车，经历了短暂的幸福生活。 好景不长，虎妞产后染病，祥子为给虎妞治病，把车又抵押了出去。虎妞母子最终双双亡故，一切都竹篮打水，祥子再次孑然一身。无数次的打击让祥子看到了生活的严酷，他亦步亦趋，在未知的命运之路上艰难地走下去…… ', '现实主义话剧', '底层·悲剧  ', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/25/49a71d98d934e3b2_60_85.jpg');
INSERT INTO `sheet1` VALUES ('水月洛神', '佟睿睿', '120分钟', '以曹植《洛神赋》为蓝本，舞者长袖回旋，展现曹植与洛神的凄美爱情。舞台融合魏晋风雅，水袖如云烟飘逸，再现千年诗意。', '民族舞剧', '古典爱情·神话', '国风', '悲剧', 'https://img.chinaticket.com/admin/2024/12/10/3c79ad2da1c6b557_60_85.jpg');
INSERT INTO `sheet1` VALUES ('无名之辈', '葛俊逸', '90分钟', '外卖骑手、超市收银员、建筑工人在城市夹缝中挣扎。舞台用机械重复的肢体语言，展现普通人被生活支配的疲惫与无声呐喊。', '现代舞剧', '都市·现实主义', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/04/11/ba4693ad935f510b_60_85.jpg');
INSERT INTO `sheet1` VALUES ('赵氏孤儿', '徐俊', '150分钟', '程婴为救赵氏孤儿献出亲儿，多年后孤儿复仇。舞台引入“程子灵魂”视角，探讨牺牲与救赎，最终程婴在亡子面前忏悔。  ', '音乐剧', '古典改编·复仇·人性', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/05/13/2f9f8e808e4d1c85_60_85.jpg');
INSERT INTO `sheet1` VALUES ('一撇一捺', '谢欣', '90分钟', '拆解“人”字结构，舞者用身体碰撞展现记忆与情感的连接。舞台极简，以肢体语言探讨人与人相遇、别离的哲学命题。 ', '现代舞剧', '抽象·人文', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/24/b123b2cfb788e245_60_85.jpg');
INSERT INTO `sheet1` VALUES ('昆仑问道', '孔德辛、郭海峰​', '120分钟', '舞剧《昆仑问道》以青藏公路与道班工人为切入口，通过守护青藏公路的道班工人一家的命运抉择，展现“两路”精神。该剧以记者“路寻”返乡的视角为线索，串联起“一封家书、一把铁锹、一条道路”背后的动人故事，生动塑造了几代道班工人在雪域高原无私奉献、艰苦筑路的群像', '舞剧', '现实主义·精神传承​', '国风', '正剧', 'https://img.chinaticket.com/admin/2025/05/19/5f28bef5e7bca16a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('我爱桃花', '任鸣', '90分钟', '该剧出自金牌编剧邹静之之手，剧情取材于明代崇祯年间刊行的拟话本小说集《型世言》。全剧由一个恋人间会错了意的误会起因，用寓言的形式演绎了一个“戏中戏”——现代的剧团排演古代的故事，男女主演因为对于剧中情人关系的争论而排不下去。', '话剧', '爱情喜剧', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/06/13/61cd968efef7397a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('物理学家', '王剑男', '120分钟', '该剧改编自瑞士剧作家迪伦马特的经典剧作，讲述了三位天才物理学家被禁锢于精神病院的荒诞故事，探讨科学伦理与人性的问题。', '话剧', '科学·伦理​', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/23/05fd61149f88b799_60_85.jpg');
INSERT INTO `sheet1` VALUES ('卿卿误我', '郭德纲', '待定', '改编自郭德纲同名单口相声，讲述民国时期富家千金白玉雪与落魄书生李伟的爱情悲剧。舞台以西湖为背景，融合民国美学，展现跨越阶级的炽热爱恋与命运捉弄。', '民国爱情话剧', '爱情·阶级冲突', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/25/f788fcc2453af8c2_60_85.jpg');
INSERT INTO `sheet1` VALUES ('四世同堂', '田沁鑫', '120分钟', '北平小羊圈胡同祁家、钱家、冠家在抗战时期的命运浮沉。老舍原著恢弘展现家国情怀，舞台以四合院结构象征时代变迁，最终祁老人面对家破人亡，仍坚守“四世同堂”的信念。  ', ' 话剧', '古典改编·家族抗战', '华语', '悲壮', 'https://img.chinaticket.com/admin/2025/06/30/802ba013d99bfbdb_60_85.jpg');
INSERT INTO `sheet1` VALUES ('平潭映象', '杨丽萍', '120分钟', '以福建平潭“海坛先民”传说为蓝本，讲述“君山王”与“平潭蓝”抵抗外敌的故事。舞台融合妈祖信仰、藤牌操等非遗元素，采用全息投影打造海洋奇幻史诗。', '民俗舞剧', '海洋·两岸文化 ', '国风', '正剧', 'https://img.chinaticket.com/admin/2025/07/03/2e2751df806c1b15_60_85.jpg');
INSERT INTO `sheet1` VALUES ('哈姆雷特', '林赛·特纳', '209分钟', '丹麦王子哈姆雷特为父复仇，在“生存还是毁灭”的挣扎中揭露宫廷阴谋。舞台常以骷髅、镜像等象征人性困境，最终以血染王庭的悲剧收场。', '话剧', '古典改编·复仇·人性', '英式', '悲剧', 'https://img.chinaticket.com/admin/2025/07/04/10d103394e544fc0_60_85.jpg');
INSERT INTO `sheet1` VALUES ('婚姻情境', '过士行', '100分钟', '约翰与玛丽安，一对登上《妇女时尚世界》的模范夫妻。 『我们几乎不吵架，就算吵也是理智地听对方说些什么，然后再达成合理的妥协。』 然而突然有一天，这座中产阶级高知家庭的城堡轰然倒塌。 『我想要甩掉你已经足足想了四年。』 『我不在乎告诉你我有多讨厌这两个愚蠢、放纵、没大脑、懒惰、自私的女儿。』 他们分居、离婚，与不同的情人恋爱、再婚，却永远也找不到灵肉合一的完美婚姻。 『你永远不能向一个人保证忠实，无论你忠实与否。』 『说到感情，我们都是文盲。』 匆匆又数年，在一个晴朗的午后，约翰与玛丽安分别避开自己在法律意义上的伴侣，又一次幽会在一起…… 『这是一场属于心灵层次的集体性交。』', '心理话剧', ' 婚姻·两性关系  ', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/07/12/64d20b2c3b891c49_60_85.jpg');
INSERT INTO `sheet1` VALUES ('婿事待发', '何念', '120分钟', '美国留学的若岚年近30仍未找到归宿，在一次爬山途中认识了年近七旬的Andy，二人彼此倾心。但随着二人婚事临近，男友提出希望拜访女方父母，而若岚深知，男友的年纪对于自己观念传统的家庭来说是绝对的禁忌。 为了掩饰男友的身份，若岚请求妹妹若雪编造谎言企图瞒过家人，随着各种不同身份的人物接连出现，麻烦越来越大，撒的谎也越来越多……', '都市喜剧', ' 家庭·荒诞  ', '华语', '喜剧', 'https://img.chinaticket.com/admin/2025/07/15/27e27b781c7c9d47_60_85.jpg');
INSERT INTO `sheet1` VALUES ('天空之城', '宫崎骏（动画电影原版） / 赵淼（舞台剧改编版）', '110分钟', ' 古老帝国拉普达是一座漂浮在空中的巨大的机器岛，传说那里已经无人居住，蕴藏着巨大的财富。因此，无论军方还是海盗，都在找寻着这座传说中的飞行岛。 矿工巴鲁这天偶遇拉普达继承人希达，两人一见如故。因为希达身上有找寻拉普达帝国的重要物件飞行石，军方和海盗两帮人马都在追杀希达。巴鲁带着希达一起逃亡，最终都没有逃出军方的手中，希达被军队掳走了。 为救朋友，只能选择与海盗合作。巴鲁与海盗成功救出了希达，同时，他们也发现了军方的邪恶计划。为了阻止军方邪恶计划的实施，他们和海盗一起踏上了寻找拉普达之旅。', '奇幻舞台剧', '冒险·环保 ', '日式', '正剧', 'https://img.piaoniu.com/my_file/9a72ed0d-e0f0-4eea-a4cb-90d90e80bf0b.jpg');
INSERT INTO `sheet1` VALUES ('笑傲江湖', '田沁鑫', '180分钟', '令狐冲从华山派弃徒到恒山掌门，在《广陵散》琴声里看透正邪之争。舞台用悬吊的剑阵象征江湖险恶，当他与任盈盈合奏“笑傲江湖”，观众听见的是金庸的武侠禅意。', '武侠话剧', '武侠江湖', '国风', '正剧', 'https://img.piaoniu.com/my_file/15cf4a54-d111-43f4-8164-3a98dc02df2e.jpg');
INSERT INTO `sheet1` VALUES ('花样年华', '王家卫（电影原版） / 李建军（舞台剧版）', '90分钟', '1962年香港，周慕云与苏丽珍发现配偶出轨后，在旗袍店和面摊试探彼此。雨伞、高跟鞋、未点燃的烟，成为他们克制的欲望符号——有些爱，只能停在“如果”。', '意象话剧', '禁忌之恋', '港风', '悲剧', 'https://img.piaoniu.com/my_file/cd79327f-1a42-4c0f-b346-953c76870efd.jpg');
INSERT INTO `sheet1` VALUES ('无名的人', 'László Lugossy（匈牙利原版） / 杨婷（中文版）', '93（原版）/100分钟（中文版）', '二战后，匈牙利士兵用假名返乡，却被官僚系统拒绝承认身份。他在档案室与街头游荡，用脚写字、用影子签名——当名字成为枷锁，无名者反而最自由。  ', '荒诞话剧', ' 身份·体制  ', '欧式', '正剧', 'https://img.piaoniu.com/my_file/321a3217-3f0e-42ac-ac08-85cf8bc4126f.jpg');
INSERT INTO `sheet1` VALUES ('花好月圆', '吴回（1956年香港电影原版） / 高满堂（舞台剧版）', '120分钟', '采莲女张丽英被父母许给三户人家，糊涂县官让三人比文招亲。穷书生、土财主、暴发户在公堂上斗诗，当丽英假死试探，唯有真爱愿为她收尸——月圆夜，花好人团圆。', '古装喜剧', '爱情讽刺', '港风', '喜剧', 'https://img.piaoniu.com/my_file/45d1d2ec-6d7f-462f-beda-3c4a2d235e8b.jpg');
INSERT INTO `sheet1` VALUES ('倒数时刻', '林奕华', '105分钟', '过气歌手在生日夜回顾三十年，CD机播完最后一首歌，时间开始倒流。舞台用旋转时钟分割场景，当他与年轻自己对唱——原来人生最痛的悔恨，是“我本可以”。', '音乐话剧', '中年危机', '港风', '悲喜交织', 'https://img.piaoniu.com/poster/cc7f1b59835b213c07da73aaf7bc217c6c8f4c80.jpg');
INSERT INTO `sheet1` VALUES ('百合花', '孟京辉', '80分钟', '改编自茹志鹃小说，新婚小媳妇用百合花被裹伤员，文工团员却用枪逼她认夫。舞台用白床单象征纯洁与暴力，当枪声击碎百合——战争里没有新人，只有亡灵。', '先锋话剧', '战争·人性', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/04/24/7d5842a0040bd9f6_60_85.jpg');
INSERT INTO `sheet1` VALUES ('七平米', '赵淼', '90分钟', '北京胡同一间7㎡的出租屋里，北漂编剧、快递小哥、保洁阿姨轮流登场。他们把床板当舞台、尿盆当道具，在马桶盖上谈理想，在灯泡下分泡面。当拆迁通知贴上墙，7㎡的笑声与哭声一起被挖掘机碾碎。  ', '小剧场话剧', '都市现实', '华语', '悲喜交织', 'https://img.chinaticket.com/admin/2025/06/14/35c5f0880585bbdf_60_85.jpg');
INSERT INTO `sheet1` VALUES ('夜行者', '李建军', '100分钟', '凌晨三点的北京，代驾司机、外卖骑手、夜班护士在空荡街头擦肩而过。手机闪光灯变成舞台追光，他们的独白交织成一首城市夜行诗：当最后一盏路灯熄灭，谁还在替城市守夜？', '纪实肢体剧', '都市群像', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/16/90adbdf9f8c830c3_60_85.jpg');
INSERT INTO `sheet1` VALUES ('曹雪芹', '王晓鹰', '150分钟', '从江宁织造府的锦衣少年到西山破庙的潦倒书生，曹雪芹用十年血泪写就《红楼梦》。舞台以一纸《废艺斋集稿》为线索，让他在“悼红轩”里与宝玉、黛玉重逢——原来大观园是他用余生造的一场梦。  ', '传记话剧', '历史·文学', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/06/21/3ebd457a70f66204_60_85.jpg');
INSERT INTO `sheet1` VALUES ('景阳冈', '徐孟珂', '120分钟', '京剧武戏《景阳冈》再现武松打虎经典。锣鼓点中，武松醉卧青石，猛虎扑来，三十二个“吊毛”翻跌一气呵成；当哨棒折、虎毙命，英雄也跌坐在夕阳里，长叹“世间恶虎何止一只”。', '京剧', '武侠传奇', '国风', '正剧', 'https://img.chinaticket.com/admin/2025/06/16/43eef462b139a7f7_60_85.jpg');
INSERT INTO `sheet1` VALUES ('梦华录', '黄盈', '130分钟', '汴京夜市灯火如昼，茶铺老板娘赵盼儿、厨娘孙三娘、琵琶高手宋引章合伙开酒楼。舞台用转台+投影复原《清明上河图》，把勾栏瓦舍、茶汤巷陌搬到观众眼前——繁华背后是一碗茶汤煮出的女性觉醒。', '新国风音乐剧', '古代·女性成长', '国风', '喜剧', 'https://img.chinaticket.com/admin/2025/06/19/1a18ac1412d16940_60_85.jpg');
INSERT INTO `sheet1` VALUES ('奥赛罗', '克里斯蒂安·陆帕', '170分钟', '摩尔人将军奥赛罗在威尼斯与苔丝狄蒙娜相爱，却被旗官伊阿古用一块手帕种下猜忌。当真相揭开，将军扼死爱人再自刎——爱有多炽烈，嫉妒就有多锋利。', '悲剧话剧', '嫉妒与背叛', '意式', '悲剧', 'https://img.chinaticket.com/admin/2025/06/27/c0e2591ff4d3de6c_60_85.jpg');
INSERT INTO `sheet1` VALUES ('马叙伦', '黄先纲', '140分钟', '从清末秀才到新中国首任教育部部长，马叙伦一生三易其志：保路运动、抗日救亡、教育救国。舞台用三把交椅象征三段人生，当他在开国大典上按下升旗按钮，灯光照亮的是一代知识分子的百年长路。', '传记话剧', '近代史', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/24/21b7576d3046c8fa_60_85.jpg');
INSERT INTO `sheet1` VALUES ('落坡岭', '王剑男', '120分钟', '京郊小站落坡岭，老站长、信号员、卖票阿姨守着一天只有三趟的慢车。当高铁呼啸而过，他们最后一次拉响汽笛——汽笛声里，是一个时代对另一个时代的告别。', '现实主义话剧', '铁路·时代变迁', '华语', '悲喜交织', 'https://img.chinaticket.com/admin/2025/07/09/be18ae658863fd21_60_85.jpg');
INSERT INTO `sheet1` VALUES ('翻山海', '朱虹璇（话剧九人团队）', '95分钟', '民国十九年，广东三山女子学校校长陆斯年决心组建中国初代女子篮球队，教练竟是她多年前出走的姊妹第五青。她们招募了一群毫无篮球经验的队员：富家千金阮风竹、逃家插班生周苔、乖乖女谢宝花、近视眼苏敏，以及后勤师傅洪道衷。这支队伍在偏见与阻力中集结，用篮球对抗时代的桎梏，最终在赛场上证明女性力量。  ', '话剧', '女性成长·体育励志  ', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/07/05/2f0d98f14f4cdd5b_60_85.jpg');
INSERT INTO `sheet1` VALUES ('山海情', '孙超', '150分钟', '宁夏西海固“吊庄移民”二十年，黄沙漫天变绿洲。舞台用可升降的黄土墙与真实麦苗，让马得福、水花们把扶贫故事唱成信天游——穷山沟长出金沙滩，山海有情，人间值得。 ', '现实题材音乐剧', '扶贫·时代变迁', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/07/04/813761ee69e6f090_60_85.jpg');
INSERT INTO `sheet1` VALUES ('于成龙', '王晓鹰', '160分钟', '清朝廉吏于成龙骑驴入广西，一纸《严禁馈送檄》斩断官场陋规。舞台用竹简与油灯象征清与浊，当他在暴雨中为百姓跪求开闸，灯油将尽，竹简却亮起微光——廉者，民之灯也。  ', '历史话剧', '廉政', '国风', '正剧', 'https://img.chinaticket.com/admin/2025/07/14/993e389db3371a8f_60_85.jpg');
INSERT INTO `sheet1` VALUES ('成·婴', '徐俊', '90分钟', '汶川地震后，妇产科医生在帐篷里迎来震后第一个新生儿。舞台用摇晃的吊瓶和心跳声做节拍，当婴儿啼哭穿透余震，废墟上开出了第一朵希望的花。', '纪实话剧', '灾难·新生', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/07/11/2e96988538e9f81a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('莫里哀', '洛朗·拉菲特（法语原版）／杨婷（中文版）', '130分钟', '法国喜剧之父莫里哀用一生嘲笑权贵，却在舞台上咳血而亡。戏中戏《无病呻吟》与现实交错，当最后一幕落下，观众分不清是莫里哀在演病人，还是病人在演莫里哀。', '传记喜剧', '艺术人生', '法式', '喜剧', 'https://img.piaoniu.com/poster/9b0bf296f0f63a363e92681304d9477bdc5a74ff.jpg');
INSERT INTO `sheet1` VALUES ('飞天', '易立明', '120分钟', '该剧以 莫高窟壁画艺术 为灵感，将以敦煌研究院首任院长 常书鸿先生 为代表的一代代敦煌文化守护者的故事浓缩为 “守护人” 和 “赶路人” 两个典型形象，串联起《鹿王本生》《千钉求法》《入海求珠》《舍身饲虎》四则经典故事。 20世纪30年代， 守护人 在洞窟只身面对恶劣气候条件和人类掠夺毁坏的威胁，以单薄之躯、一己之力捍卫莫高窟的壁画；在法国留学的 赶路人 因意外看到敦煌壁画画册，毅然投身于拯救和保护文化遗产的事业中，历经颠沛流离，目睹世界战乱频出、疾病横行肆虐，最后回到魂牵梦萦的敦煌。 男女主一动一静，身处不同地理坐标，在 现实与梦境 的时空交叠中，演绎了一场从未谋面的爱情，以及跋涉万里、坚守信念的动人故事。', '多媒体舞剧', '敦煌·丝路', '国风', '正剧', 'https://img.piaoniu.com/my_file/cdf2c350-262d-4371-b2c9-3ec126bcfe51.jpg');
INSERT INTO `sheet1` VALUES ('英歌', '陈飞', '110分钟', '上世纪三十年代末，父亲前往南洋，杳无音讯；母亲含辛茹苦抚养儿子成人。 元宵夜，儿子敲击父亲留下的英歌槌，进入幻境时空，这里如梦如幻、光怪陆离。在漫天硝烟中，儿子明白了父亲怀抱的家国大义，解开了母亲毕生的心结，自己也得以感悟、成长。 三江出海，一纸归潮。传承的血脉，浪打不息，风吹不散！ “中华战舞”火爆出圈！ 英歌槌响，即是吾乡。', NULL, '非遗传承', '国风', '正剧', 'https://img.chinaticket.com/admin/2025/04/23/06b2f2e30a206888_60_85.jpg');
INSERT INTO `sheet1` VALUES ('杜甫', '冯远征', '150分钟', '唐玄宗天宝年间，杜甫与诗友李白、高适同游汴梁，正在单父台斗诗游乐之际，安史之乱突起，惊破霓裳羽衣曲，也惊散了诗朋酒侣，李白寻归庐山，高适入哥舒幕，杜甫也奔赴长安。 数年后，杜甫仕途失意，携家眷走巴蜀，路遇成州小吏抓兵，讲理不得反遭奚落，遂转道投奔世交严武，又见其兵骄将悍，恐难相处，悄然折向成都，在浣花溪畔盖个草堂，以为栖身之所。 得知杜甫境况，正在巴蜀一带任职的高适和严武先后造访草堂。高适推衣送食，严武更有过之，诚邀杜甫入幕，又举荐杜甫为参谋，并工部员外郎。 杜甫满心高兴，入了严武幕府。不多久，二人却因政见不合，个性相悖，爆发龃龉，怒目相向以至不欢而散。 退居草堂的杜甫，心情抑郁，新朋苏涣来访，为他剖析冲突的根由，杜甫内心折服。时值秋令，狂风大作，茅屋为秋风所破。诗人虽为风雨所苦，犹然发出生命的强音：“安得广厦千万间，大庇天下寒士俱欢颜，风雨不动安如山！呜呼！何时眼前突兀见此屋？吾庐独破受冻死亦足！” 杜甫由此出走湖湘。天阙换过几朝皇帝，杜甫在梦幻中遇见苏涣、当年诗友李白高适，挚友严武，十年离乱，沧桑变幻，物是人非……杜甫表示，蓝天白云才是鸟的故乡。 江湖暴涨，杜甫死于江浒，唯有他的诗歌永远回响在辽阔的中华大地，成为中华民族的集体记忆。', '诗剧', '诗人传记', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/07/11/4c71b1de1bd34689_60_85.jpg');
INSERT INTO `sheet1` VALUES ('雷雨', '濮存昕', '160分钟', '周公馆的暴风雨夜，三十年爱恨情仇在一声炸雷中爆发。繁漪的疯狂、周萍的懦弱、四凤的绝望，像八月的雷雨倾泻而下——当枪声响起，所有人都被命运淋成落汤鸡。', '现实主义话剧', '古典改编·现实主义·家庭', '华语', '悲剧', 'https://img.chinaticket.com/admin/2024/12/12/fd7bd2176a5e5d72_60_85.jpg');
INSERT INTO `sheet1` VALUES ('女仆', '孟京辉', '100分钟', '让·热内同名剧本的孟氏重构。两个女仆在主人卧室里轮流扮演“女主人”，高跟鞋、香水、蕾丝裙成为她们颠覆阶级的武器。当游戏失控，镜子碎成利刃，身份与欲望一起流血。', '先锋话剧', '阶级·欲望', '法式', '悲剧', 'https://img.chinaticket.com/admin/2025/04/29/af8ce4cae7655576_60_85.jpg');
INSERT INTO `sheet1` VALUES ('出马', '赵淼', '95分钟', '东北萨满少年被黄仙附体，在跳神鼓点里看见太爷爷的马刀。舞台用萨满面具与LED火盆，把“请神”“送神”做成一场现代青年的自我救赎——当鼓声骤停，少年终于喊出“我是人，不是仙”。', '民俗肢体剧', '萨满·成长', '华语', '正剧', 'https://img.chinaticket.com/admin/2025/06/16/bd27a5d7774c51b6_60_85.jpg');
INSERT INTO `sheet1` VALUES ('茶馆', '焦菊隐（经典版）／濮存昕（2024版）', '180分钟', '从清末到抗战，裕泰茶馆里三教九流来了又走。王利发的算盘声、常四爷的叹息、秦二爷的理想，在茶香与炮火中飘散。当“莫谈国事”的纸条被撕下，一座茶馆就是一部中国近代史。', '现实主义话剧', '现实主义·时代变迁', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/18/db1eb1805df4dca6_60_85.jpg');
INSERT INTO `sheet1` VALUES ('朱鹮', '佟睿睿', '100分钟', '从盛唐宫廷到现代湿地，朱鹮从“吉祥鸟”到“濒危物种”。舞剧用白色羽纱与投影还原朱鹮的优雅与哀鸣，当最后一只朱鹮倒在猎枪下，观众的掌声变成一声叹息——保护，才是最好的纪念。', '民族舞剧', '环保·诗意', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/06/11/02f5ad4bd0e1f5a5_60_85.jpg');
INSERT INTO `sheet1` VALUES ('浪潮', '何念', '130分钟', '改编自真实事件，五天之内，高中老师用“纪律、集体、行动”把班级变成微型纳粹。白衬衫、红袖标、整齐口号……当学生把老师推上“领袖”神坛，浪潮却变成海啸——实验失控，青春见血。', '纪实话剧', '校园·极权实验', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/06/58e7c0cc37f3234c_60_85.jpg');
INSERT INTO `sheet1` VALUES ('卡门', 'Valentina Peleggi（马德里皇家歌剧院版）／许忠（上海大剧院版）', '150分钟', '塞维利亚的艳阳下，吉卜赛女郎卡门用玫瑰与匕首挑逗士兵唐·何塞。当爱情变成枷锁，她用死亡唱出自由——斗牛场的欢呼声中，卡门倒在血泊，像一朵不肯枯萎的野玫瑰。', '歌剧', '爱情·自由', '西班牙', '悲剧', 'https://img.chinaticket.com/admin/2025/06/14/4f0f83b1c34b6e1f_60_85.jpg');
INSERT INTO `sheet1` VALUES ('立秋', '吴京安', '160分钟', '民国山西票号“丰德”面临倒闭，老东家马洪翰在祖训与时代夹缝中挣扎。算盘声与火车汽笛交织，晋商五百年辉煌在“立秋”这天轰然落幕——当银票化作纸钱，山西商人终于学会向时代低头。', '历史话剧', '晋商·时代更迭', '华语', '悲剧', 'https://img.chinaticket.com/admin/2025/06/24/836e11df7f816f6a_60_85.jpg');
INSERT INTO `sheet1` VALUES ('海鸥', '赖声川', '165分钟', '乡村庄园的夏夜，青年作家特里波列夫的剧本被母亲嘲笑，心爱的妮娜奔向名作家怀抱。契诃夫用四幕时间让理想与爱情一起坠落——当妮娜十年后归来，只剩一只折翼的海鸥在湖面上漂浮。', '现实主义话剧', '艺术·爱情幻灭', '俄式', '悲剧', 'https://img.chinaticket.com/admin/2025/07/08/0b90a78d5e585dfa_60_85.jpg');
INSERT INTO `sheet1` VALUES ('唐璜', 'Damiano Michieletto（萨尔茨堡版）／陈薪伊（中）', '160分钟', '风流贵族唐璜用千首情歌征服欧洲，却在石像晚宴上被地狱拖走。莫扎特的音乐让浪子回头成为不可能的任务——当最后一声和弦落下，观众听见的是自由的挽歌。', '歌剧', '浪子·道德审判', '意式', '悲剧', 'https://img.chinaticket.com/admin/2025/07/04/61a4a0cab40d1088_60_85.jpg');
INSERT INTO `sheet1` VALUES ('青衣', '王晓鹰', '120分钟', '京剧名角筱燕秋为“嫦娥”一角疯魔，从舞台皇后到精神病院，水袖与输液管一起飞舞。当她在病房里唱起“长袖善舞”，观众分不清谁是嫦娥，谁是疯子——戏比天大，命比纸薄。', '现代京剧', '艺术', '国风', '悲剧', 'https://img.chinaticket.com/admin/2025/07/11/2df2c9bbb085a2fd_60_85.jpg');
INSERT INTO `sheet1` VALUES ('蝶变', '赵淼', '95分钟', '实验室里，蝴蝶基因与人类DNA意外融合。少女长出彩色翅膀，却在玻璃箱里看见自己破碎的倒影。肢体与投影交织成一场“美丽与恐怖”的蜕变——当翅膀展开，观众听见基因在尖叫。', '科幻肢体剧', '基因·科幻', ' 华语', '悲剧', 'https://img.piaoniu.com/my_file/b3a6ca51-2036-4a3e-981d-1b0eb201242e.jpg');
INSERT INTO `sheet1` VALUES ('宝玉', '林奕华 ', '160分钟', '21 世纪写字楼里的“宝玉”做PPT、开例会，却在午休梦回大观园。现代职场与《红楼梦》交错，当宝玉在玻璃幕墙前喊出“我要做无用之人”，观众听见的是都市青年的集体叹息。', '原创音乐剧', '都市·红楼新编', '华语', '悲喜交织', 'https://img.piaoniu.com/my_file/02aca855-40a5-4b1e-8578-b1ebfc4d936f.jpg');
